    end
    qulv = 0;
    if(steer<=qulv_angle(end,1) && steer>=qulv_angle(1,1))
        qulv = interp1(qulv_angle(1:end,1), qulv_angle(1:end,2), steer);
    else
        if( steer>qulv_angle(end,1) )
            qulv = qulv_angle(end,1);
        else
            qulv = qulv_angle(1,1);
